CREATE TABLE "met-galaxy_artwork" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "met-galaxy_artwork_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"objectId" integer NOT NULL,
	"title" varchar(500),
	"artist" varchar(500),
	"date" varchar(100),
	"medium" varchar(500),
	"primaryImage" varchar(1000),
	"department" varchar(200),
	"culture" varchar(200),
	"createdAt" timestamp with time zone,
	"additionalImages" text,
	"objectUrl" varchar(500),
	"isHighlight" boolean,
	"artistDisplayBio" text,
	"objectBeginDate" integer,
	"objectEndDate" integer,
	"creditLine" text,
	"classification" varchar(200),
	"artistNationality" varchar(200),
	"primaryImageSmall" varchar(1000),
	"description" text,
	"importedAt" timestamp with time zone DEFAULT CURRENT_TIMESTAMP NOT NULL
);
